package medium;

public class LongestPalindromeSubString {
    public static void main(String[] args) {
        LongestPalindromeSubString tester = new LongestPalindromeSubString();
        System.out.println(tester.longestPalindromemySolution(""));
        System.out.println(tester.longestPalindromeLeetCodeSolution(""));
    }

    // My solution Start
    private String longestPalindromemySolution(String s) {
        if(s.length() == 1 || s.length() == 0) return s;

        String[] characters = s.split("");
        String palindromeString = "";
        for (int i = 0; i < characters.length; i++) {
            StringBuilder proposedPalindromeString = new StringBuilder();
            proposedPalindromeString.append(characters[i]);
            for (int j = 0; j < characters.length; j++) {
                if(j > i) {
                    proposedPalindromeString.append(characters[j]);
                    StringBuilder reverseString = new StringBuilder(proposedPalindromeString.toString()).reverse();
                    if (reverseString.toString().equals(proposedPalindromeString.toString())) {
                        palindromeString = reverseString.length() > palindromeString.length() ? reverseString.toString() : palindromeString;
                    }
                }
            }
        }

        return palindromeString.length() > 0 ? palindromeString : String.valueOf(s.charAt(0));
    }
    //My solution ends


    // Leet Code solution Start
    private String longestPalindromeLeetCodeSolution(String s) {
        if (s == null || s.length() < 1) return "";
        int start = 0, end = 0;
        for (int i = 0; i < s.length(); i++) {
            int len1 = expandAroundCenter(s, i, i);
            int len2 = expandAroundCenter(s, i, i + 1);
            int len = Math.max(len1, len2);
            if (len > end - start) {
                start = i - (len - 1) / 2;
                end = i + len / 2;
            }
        }
        return s.substring(start, end + 1);
    }

    private int expandAroundCenter(String s, int left, int right) {
        int L = left, R = right;
        while (L >= 0 && R < s.length() && s.charAt(L) == s.charAt(R)) {
            L--;
            R++;
        }
        return R - L - 1;
    }
    // Let code solution ends.
}
